import React from "react";
import Board from "./Board";

/**
 * turn_index: the current game turn of the user
 * squares_filled: the number of letter inputs provided by the user
 * current_word: a representation of the current word typed by the user
 */
type GameState = {
    turn_index: number
    squares_filled: number,
    current_letter_index: string,
    current_user_word: Array<String>
  }


class Game extends React.Component {

    private _word_to_guess: string = "";
    private _word_length: number = 0;

    state: GameState = {
        turn_index: 1,
        squares_filled: 1,
        current_letter_index: "1",
        current_user_word: Array<String>()
    }

    constructor(props: {} | Readonly<{}>){
        super(props);

        //This is where we will query our API to get a word
        this._word_to_guess = "Aobdsddssd";

        this._word_length = this._word_to_guess.length;

        let array = this.state.current_user_word;
        this.setState({current_user_word: array.push(this._word_to_guess.charAt(0))});
        console.log(this.state.current_user_word);

    }


    updateUserWord(letter_input: string, id_to_be_modified: number): void{

        let newArray: Array<String> = this.state.current_user_word;

        //handling the case where the user wanted to delete the last letter of its word
        if (letter_input === '.'){
            newArray.pop();
        }
        else {
            newArray.push(letter_input);
        }
        this.setState({current_user_word: newArray});
        console.log("Nouveau mot du user: " + this.state.current_user_word);
    }


    //On suppose que l'implémentation sera de telle sorte à ce que 
    //dès qu'un user appuie sur entrée, c'est pour vérifier un mot complet
    checkAnswer(){

        let user_answer: String = this.state.current_user_word.join('');
        let word_to_guess_from: Array<String> = this.state.current_user_word;
        //TODO: comparaison exacte

        for (let i = 1; i < this._word_length; i++){
            let index = user_answer[i].indexOf(this._word_to_guess);
            //Tant que la lettre apparaît à partir de la position précisée (1 initialement)
            while (index !== -1){
                //cas où la lettre se trouve dans le mot à trouver, mais à une position différente
                if (this._word_to_guess[index] !== user_answer[i]){
                    word_to_guess_from[i] = '.';
                    //coloration dans le DOM
                    let yellow_square_index = this._word_length*(this.state.turn_index - 1) + i;
                    let yellow_square = document.getElementById(yellow_square_index.toString()) as HTMLElement;
                    yellow_square.style.backgroundColor = "red";
                }
                else {
                    word_to_guess_from[i] = this._word_to_guess[index];
                    //coloration dans le DOM
                }
            }
        }



        //getting to the next turn
        this.setState({turn_index: this.state.turn_index+1});
    }
    /**
     * 
     * @param keyPressed
     * @param currentSquareId 
     */
    modifyLetter(keyPressed: string, currentSquareId: number): void{

        const square = document.getElementById(currentSquareId.toString()) as HTMLElement;
        square.innerHTML = keyPressed.toUpperCase();

        //This allows us to use the modifyLetter method to handle the backspace presses
        let magic = keyPressed === '.' ? -1 : 1
        this.setState({squares_filled: this.state.squares_filled + magic});
        this.updateUserWord(keyPressed, this.state.squares_filled + magic);
        //TODO: une fonction de création d'un nouvel array avec la nouvelle lettre: args = lettre et id courant


    }
    /**
     * This method is used to handle special characters: Enter, and Backspace, 
     * as they allow the user to either validate a word, or delete a letter from it.
     * @param p the special key pressed by the user.
     */
    handleSpecialCharacters(p: String): void{
        if (p === "Enter"){
            if (this.state.squares_filled !== this._word_length)
                console.log("Mot incomplet");    
            else {
                this.checkAnswer();
            }
        }

        else if (p === "Backspace"){
            console.log("bp")
            if (this.state.squares_filled > 1){

                this.modifyLetter('.', this.state.squares_filled -1)

            }
        }

    }

    //Method used to handle keyboard inputs
    handleKeyboardInput(event: KeyboardEvent): void{

        let p = event.key;

        if (this.state.squares_filled !== this._word_length){

            console.log(p);

            let regexp = /^[a-zA-Z]+$/

            let a = regexp.test(p);

            if (a && p.length < 2){
                console.log("YES", p);
                this.modifyLetter(p, this.state.squares_filled);
            }
        }
        this.handleSpecialCharacters(p);
    }


    componentDidMount(){
        window.addEventListener("keydown", (event) => {
            this.handleKeyboardInput(event);
        })
    }

    render(){

        return (
            <div>
                <Board word_length={this._word_length} word={this._word_to_guess} />
            </div>
        );
    }
}





export default Game;
