import React from "react";
import Board from "./Board";

/**
 * turn_index: the current game turn of the user
 * squares_filled: the number of letter inputs provided by the user
 * current_word: a representation of the current word typed by the user
 */
type GameState = {
    turn_index: number
    squares_filled: number,
    current_letter_index: string
  }


class Game extends React.Component {

    private _word_to_guess: string = "";
    private _word_length: number = 0;

    state: GameState = {
        turn_index: 1,
        squares_filled: 1,
        current_letter_index: "1",
    }

    constructor(props: {} | Readonly<{}>){
        super(props);

        //This is where we will query our API to get a word
        this._word_to_guess = "Zobdsddssd";

        this._word_length = this._word_to_guess.length;

    }



    //On suppose que l'implémentation sera de telle sorte à ce que 
    //dès qu'un user appuie sur entrée, c'est pour vérifier un mot complet
    checkAnswer(){

        //TODO: récupérer dans le DOM les lettres associées
    }
    /**
     * 
     * @param keyPressed
     * @param currentSquareId 
     */
    modifyLetter(keyPressed: string, currentSquareId: number): void{

        const square = document.getElementById(currentSquareId.toString()) as HTMLElement;
        square.innerHTML = keyPressed.toUpperCase();

        //This allows us to use the modifyLetter method to handle the backspace presses
        let magic = keyPressed === '.' ? -1 : 1
        this.setState({squares_filled: this.state.squares_filled + magic})

    }
    /**
     * This method is used to handle special characters: Enter, and Backspace, 
     * as they allow the user to either validate a word, or delete a letter from it.
     * @param p the special key pressed by the user.
     */
    handleSpecialCharacters(p: String): void{
        if (p === "Enter"){
            if (this.state.squares_filled !== this._word_length)
                console.log("Mot incomplet");    
            else {
                //TODO: comparer les mots
            }
        }

        else if (p === "Backspace"){
            console.log("bp")
            if (this.state.squares_filled > 1){

                this.modifyLetter('.', this.state.squares_filled -1)

            }
        }

    }

    //Method used to handle keyboard inputs
    handleKeyboardInput(event: KeyboardEvent): void{

        let p = event.key;

        if (this.state.squares_filled !== this._word_length){

            console.log(p);

            let regexp = /^[a-zA-Z]+$/

            let a = regexp.test(p);

            if (a && p.length < 2){
                console.log("YES", p);
                this.modifyLetter(p, this.state.squares_filled);
            }
        }
        this.handleSpecialCharacters(p);
    }






    componentDidMount(){
        window.addEventListener("keydown", (event) => {
            this.handleKeyboardInput(event);
        })
    }

    render(){

        return (
            <div>
                <Board word_length={this._word_length} word={this._word_to_guess} />
            </div>
        );
    }
}





export default Game;
