import React from "react";
import Board from "./Board";

type GameState = {
    turns_left: number,
    squares_filled: 1
  }


class Game extends React.Component {

    private _word: string = "";
    private _word_length: number = 0;

    state: GameState = {
        turns_left: 6,
        squares_filled: 1
    }

    constructor(props: {} | Readonly<{}>){
        super(props);

        //This is where we will query our API to get a word
        this._word = "Zobdsddssd";
        this._word_length = this._word.length;

    }



    //Function used to modify the letter of the currentSquare
    modifyLetter(keyPressed: string, currentSquareId: string): void{

        const square = document.getElementById(currentSquareId) as HTMLElement;
        square.innerHTML = keyPressed.toUpperCase();
        this.setState({squares_filled: this.state.squares_filled + 1})

    }

    //Method used to handle keyboard inputs
    handleKeyboardInput(event: KeyboardEvent): void{

        let p = event.key;

        let regexp = /^[a-zA-Z]+$/

        let a = regexp.test(p);

        console.log(a);

        if (p.length < 2 || p !== " "){
            console.log("YES", p);
            this.modifyLetter(p, this.state.squares_filled.toString())
        }
    }


    componentDidMount(){

        window.addEventListener("keypress", (event) => {

            this.handleKeyboardInput(event);

        })

    }

    render(){

        return (
            <div>
                <Board word_length={this._word_length} word={this._word} />
            </div>
        );
    }

    zob() {

    }


}





export default Game;
