import React from "react";
import Board from "./Board";

type GameState = {
    turns_left: number,
    squares_filled: 1
  }


class Game extends React.Component {

    private _word: string = "";
    private _word_length: number = 0;

    state: GameState = {
        turns_left: 6,
        squares_filled: 1
    }

    constructor(props: {} | Readonly<{}>){
        super(props);

        //This is where we will query our API to get a word
        this._word = "Zobdsddssd";
        this._word_length = this._word.length;

    }



    /**
     * 
     * @param keyPressed The
     * @param currentSquareId 
     */
    modifyLetter(keyPressed: string, currentSquareId: string): void{

        const square = document.getElementById(currentSquareId) as HTMLElement;
        square.innerHTML = keyPressed.toUpperCase();
        this.setState({squares_filled: this.state.squares_filled + 1})

    }
    /**
     * This method is used to handle special characters: Enter, and Backspace, 
     * as they allow the user to either validate a word, or delete a letter from it.
     * @param p the special key pressed by the user.
     */
    handleSpecialCharacters(p: String): void{
        if (p === "Enter"){
            if (this.state.squares_filled !== this._word_length)
                console.log("Mot incomplet");    
            else {

            }
        }

        else if (p === "Backspace"){
            console.log("bp")
            if (this.state.squares_filled > 1){

                let lastModifiedSquare = (this.state.squares_filled - 1).toString()

                const square = document.getElementById(lastModifiedSquare) as HTMLElement;
                square.innerHTML = ".";
                this.setState({squares_filled: this.state.squares_filled - 1})
            }
        }

    }

    //Method used to handle keyboard inputs
    handleKeyboardInput(event: KeyboardEvent): void{

        let p = event.key;

        if (this.state.squares_filled !== this._word_length){

            console.log(p);

            let regexp = /^[a-zA-Z]+$/

            let a = regexp.test(p);

            if (a && p.length < 2){
                console.log("YES", p);
                this.modifyLetter(p, this.state.squares_filled.toString())
            }
        }
        this.handleSpecialCharacters(p)
    }




    componentDidMount(){

        window.addEventListener("keydown", (event) => {

            this.handleKeyboardInput(event);

        })

    }

    render(){

        return (
            <div>
                <Board word_length={this._word_length} word={this._word} />
            </div>
        );
    }


}





export default Game;
